                        }
                    </div>
                    <div class="col-sm">
                        <Route path="/MyCalendar/date/:id/:date"><CardPres /></Route>
                    </div>
                    </Router>
                 */}

            </div>
            <Footer />
        </div>
    )
}

export default MyCalendar
